import React from "react";
import { Link } from "react-router-dom";
import "./EditContact.css";

class EditContact extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            _id: "",
            name: "",
            phoneNumber: "",
            photo: "",
            department: "",
            tableData: Array.from({ length: 8 }, () => Array(6).fill("")), // 8 rows, 6 columns
        };
    }

    componentDidMount() {
        const location = window.location;
        const _id = location.pathname.substring(
            location.pathname.lastIndexOf("/") + 1
        );

        const contact = this.props.contacts.find((contact) => contact._id === _id);

        if (contact) {
            this.setState({
                _id: _id,
                name: contact.name || "",
                phoneNumber: contact.phoneNumber || "",
                photo: contact.photo || "",
                department: contact.department || "",
                tableData: contact.tableData || Array.from({ length: 8 }, () => Array(6).fill("")),
            });
        }
    }

    handleTableChange = (rowIndex, colIndex, value) => {
        const newTableData = [...this.state.tableData];
        newTableData[rowIndex][colIndex] = value;
        this.setState({ tableData: newTableData });
    };

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };

    update = (e) => {
        e.preventDefault();

        const { name, phoneNumber, department, photo } = this.state;

        if (name === "") {
            alert("Please provide a name");
            return;
        }

        if (!/^[0-9]{10}$/.test(phoneNumber)) {
            alert("Phone number must be exactly 10 digits");
            return;
        }

        if (department === "") {
            alert("Please provide a department");
            return;
        }

        if (photo === "") {
            alert("Please provide a valid URL for the photo");
            return;
        }

        this.props.updateContactHandler(this.state);

        // Clear the form fields
        this.setState({
            _id: "",
            name: "",
            phoneNumber: "",
            photo: "",
            department: "",
            tableData: Array.from({ length: 8 }, () => Array(6).fill("")),
        });
    };

    render() {
        const daysOfWeek = React.useMemo(() => ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"], []);
        const periods = React.useMemo(() => Array.from({ length: 8 }, (_, i) => i + 1), []);

        return (
            <div className="container-contact2">
                <Link to="/">
                    <button className="btn3">{"<"}</button>
                </Link>
                <div className="wrap-contact2">
                    <form className="contact2-form" onSubmit={this.update}>
                        <span className="contact2-form-title">Edit Contact</span>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Name"
                                type="text"
                                name="name"
                                value={this.state.name}
                                onChange={this.handleChange}
                            />
                            <span className="focus-input2" data-placeholder="NAME"></span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Phone Number"
                                type="text"
                                name="phoneNumber"
                                value={this.state.phoneNumber}
                                onChange={this.handleChange}
                            />
                            <span className="focus-input2" data-placeholder="PHONE NUMBER"></span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Department"
                                type="text"
                                name="department"
                                value={this.state.department}
                                onChange={this.handleChange}
                            />
                            <span className="focus-input2" data-placeholder="DEPARTMENT"></span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Photo URL"
                                type="text"
                                name="photo"
                                value={this.state.photo}
                                onChange={this.handleChange}
                            />
                            <span className="focus-input2" data-placeholder="PHOTO URL"></span>
                        </div>

                        {/* Editable Table */}
                        <table className="contact-table">
                            <thead>
                            <tr>
                                <th></th>
                                {daysOfWeek.map((day, index) => (
                                    <th key={index}>{day}</th>
                                ))}
                            </tr>
                            </thead>
                            <tbody>
                            {periods.map((period, rowIndex) => (
                                <tr key={rowIndex}>
                                    <td><strong>{period}</strong></td> {/* Period number */}
                                    {daysOfWeek.map((_, colIndex) => (
                                        <td key={colIndex}>
                                            <input
                                                type="text"
                                                value={this.state.tableData[rowIndex][colIndex] || ""}
                                                onChange={(e) =>
                                                    this.handleTableChange(rowIndex, colIndex, e.target.value)
                                                }
                                            />
                                        </td>
                                    ))}
                                </tr>
                            ))}
                            </tbody>
                        </table>

                        <button className="btn">Update</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default EditContact;
