function App() {
    const [contacts, setContacts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [filteredContacts, setFilteredContacts] = useState([]);
    const [isAuth, setAuth] = useState(false);

    const getContacts = async () => {
        try {
            const res = await axios.get("/api/contacts");
            setContacts(res.data);
            setFilteredContacts(res.data); // Initially set filteredContacts to include all contacts
        } catch (err) {
            console.log("Error from ContactList", err);
        }
    };

    const searchHandler = (searchTerm) => {
        setSearchTerm(searchTerm);
        if (searchTerm.length > 0) {
            const newContactList = filteredContacts.filter((contact) => {
                const searchableContent = `${contact.name} ${contact.email} ${contact.phoneNumber} ${contact.department}`;
                return searchableContent.toLowerCase().includes(searchTerm.toLowerCase());
            });
            setSearchResults(newContactList);
        } else {
            setSearchResults(filteredContacts);
        }
    };

    useEffect(() => {
        if (isAuth) {
            getContacts();
        }
    }, [isAuth]);

    return (
        <div className="App-container">
            <Router>
                {isAuth ? (
                    <>
                        <Header />
                        <Routes>
                            <Route
                                path="/"
                                element={
                                    <ContactList
                                        contacts={searchTerm.length < 1 ? filteredContacts : searchResults}
                                        getContactID={removeContactHandler}
                                        term={searchTerm}
                                        searchKeyword={searchHandler}
                                        setFilteredContacts={setFilteredContacts}
                                        allContacts={contacts}  // Pass full contacts list for filtering
                                    />
                                }
                            />
                            {/* other routes */}
                        </Routes>
                        <DarkMode />
                    </>
                ) : (
                    <Routes>
                        <Route path="/" element={<Login setAuth={setAuth} />} />
                    </Routes>
                )}
            </Router>
        </div>
    );
}

export default App;
