import React from "react";
import { Link } from "react-router-dom";

// Define constants outside the class to avoid re-creating them on every render
const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
const periods = Array.from({ length: 8 }, (_, i) => i + 1);

class EditContact extends React.Component {
    constructor(props) {
        super(props);
        const location = window.location;
        const _id = location.pathname.substring(
            location.pathname.lastIndexOf("/") + 1
        );

        // Initialize state with values from props if available
        const { name, phoneNumber, photo, department, tableData, qualification } = props.contacts.find(
            (contact) => contact._id === _id
        ) || {};

        this.state = {
            _id: _id,
            name: name || "",
            phoneNumber: phoneNumber || "",
            photo: photo || "",
            department: department || "",
            tableData: tableData || Array.from({ length: 8 }, () => Array(6).fill("")),
            qualification: qualification || "",
            errors: {
                name: "",
                phoneNumber: "",
                department: "",
                qualification: "",
                photo: ""
            }
        };
    }

    handleTableChange = (rowIndex, colIndex, value) => {
        const newTableData = [...this.state.tableData];
        newTableData[rowIndex][colIndex] = value;
        this.setState({ tableData: newTableData });
    };

    validateInputs = () => {
        const { name, phoneNumber, department, qualification, photo } = this.state;
        let errors = { name: "", phoneNumber: "", department: "", qualification: "", photo: "" };
        let isValid = true;

        if (name === "") {
            errors.name = "Please provide a name";
            isValid = false;
        }

        if (!/^[0-9]{10}$/.test(phoneNumber)) {
            errors.phoneNumber = "Phone number must be exactly 10 digits";
            isValid = false;
        }

        if (department === "") {
            errors.department = "Please provide a department";
            isValid = false;
        }

        if (!/^[A-Za-z\s.,]+$/.test(qualification)) {
            errors.qualification = "Qualification should contain only alphabets, spaces, dots, and commas";
            isValid = false;
        }

        if (photo === "" || !/^https?:\/\/.+/.test(photo)) {
            errors.photo = "Please provide a valid URL for the photo";
            isValid = false;
        }

        this.setState({ errors });
        return isValid;
    };

    update = (e) => {
        e.preventDefault();

        if (this.validateInputs()) {
            // Pass updated contact data including tableData to parent handler
            this.props.updateContactHandler(this.state);

            // Clear the form fields
            this.setState({
                _id: "",
                name: "",
                phoneNumber: "",
                photo: "",
                department: "",
                tableData: Array.from({ length: 8 }, () => Array(6).fill("")),
                qualification: "",
                errors: { name: "", phoneNumber: "", department: "", qualification: "", photo: "" }
            });
        }
    };

    render() {
        return (
            <div className="container-contact2">
                <Link to="/">
                    <button className="btn3">{"<"}</button>
                </Link>
                <div className="wrap-contact2">
                    <form className="contact2-form" onSubmit={this.update}>
                        <span className="contact2-form-title">Edit Contact</span>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Name"
                                type="text"
                                name="name"
                                value={this.state.name}
                                onChange={(e) => this.setState({ name: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="NAME"></span>
                            <span style={{ color: "red" }}>{this.state.errors.name}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Phone Number"
                                type="text"
                                name="phoneNumber"
                                value={this.state.phoneNumber}
                                onChange={(e) => this.setState({ phoneNumber: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="PHONE NUMBER"></span>
                            <span style={{ color: "red" }}>{this.state.errors.phoneNumber}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Department"
                                type="text"
                                name="department"
                                value={this.state.department}
                                onChange={(e) => this.setState({ department: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="DEPARTMENT"></span>
                            <span style={{ color: "red" }}>{this.state.errors.department}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Qualification"
                                type="text"
                                name="qualification"
                                value={this.state.qualification}
                                onChange={(e) => this.setState({ qualification: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="QUALIFICATION"></span>
                            <span style={{ color: "red" }}>{this.state.errors.qualification}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Photo URL"
                                type="text"
                                name="photo"
                                value={this.state.photo}
                                onChange={(e) => this.setState({ photo: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="PHOTO URL"></span>
                            <span style={{ color: "red" }}>{this.state.errors.photo}</span>
                        </div>

                        {/* Editable 7x9 Table */}
                        <table className="contact-table">
                            <thead>
                            <tr>
                                <th></th>
                                {daysOfWeek.map((day, index) => (
                                    <th key={index}>{day}</th>
                                ))}
                            </tr>
                            </thead>
                            <tbody>
                            {periods.map((period, rowIndex) => (
                                <tr key={rowIndex}>
                                    <td><strong>{period}</strong></td>
                                    {daysOfWeek.map((_, colIndex) => (
                                        <td key={colIndex}>
                                            <input
                                                type="text"
                                                value={this.state.tableData[rowIndex][colIndex] || ""}
                                                onChange={(e) =>
                                                    this.handleTableChange(rowIndex, colIndex, e.target.value)
                                                }
                                            />
                                        </td>
                                    ))}
                                </tr>
                            ))}
                            </tbody>
                        </table>

                        <button className="btn">Update</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default EditContact;
