import React from "react";
import {Link} from "react-router-dom";

const ContactCard = (props) => {
    const {userDetails} = props;

    const deleteContact = (id) => {
        fetch(`/api/contacts/${id}`, {method: 'DELETE'}).then((response) => {
            if (!response.ok) {
                throw new Error('Failed to delete contact');
            }
            return response.json();  // Return JSON only if the request is successful
        })
            .then(() => {
                props.clickHandler(id);  // Update the UI after successful deletion
            })
            .catch((error) => {
                console.error('Error deleting contact:', error);
            });
    };


    return (

        <div className="item">
            <img
                className="image-avtar"
                src={photo || "https://www.gravatar.com/avatar/?d=mp"} // Default image if no photo
                alt="user"
            />
            <Link to={`/contact/${userDetails.id}`}>
                <div className="content">
                    <div className="item-name">{userDetails.name}</div>
                    <div className="item-phone">{userDetails.phoneNumber}</div>
                    {/* Display phone number */}
                    <div className="item-department">{userDetails.department}</div>
                    {/* Display department */}
                </div>
            </Link>
            <Link to={{ pathname: `/edit/${userDetails.id}`, id: userDetails.id }}>
                <i className="edit-icon fa fa-edit" />
            </Link>
            <i
                className="remove-icon fa fa-remove"
                onClick={() => deleteContact(userDetails.id)} // Properly handle delete action
            />

            }

        </div>
    );
};

export default ContactCard;
