import React, { useRef } from "react";
import { Link } from "react-router-dom";
import ContactCard from "./ContactCard";

const ContactList = (props) => {
  const inputE1 = useRef("");

  const deleteContactHandler = (id) => {
    props.getContactID(id);
  };

  const renderContactList = props.contacts.map((contact) => {
    return (
        <ContactCard
            contact={contact}
            clickHandler={deleteContactHandler}
            key={contact._id}
        />
    );
  });

  const getSearchTerm = () => {
    props.searchKeyword(inputE1.current.value);
  };

  // Function to filter contacts by department
  const filterByDepartment = (department) => {
    const filtered = props.contacts.filter(contact => contact.department === department);
    props.setFilteredContacts(filtered);  // Use the passed function to update the filtered contacts
  };

  return (
      <div>
        <div className="contact-list">
                <span className="contact2-form-title2">
                    Contact list
                    <Link to="/add" className="contact2-form-add">
                        <button className="btn2">Add contact</button>
                    </Link>
                </span>

          <div className="con-length">
            Total Contacts: {props.contacts.length}
          </div>

          <div className="con-search">
            <div className="con-search-input">
              <input
                  ref={inputE1}
                  type="text"
                  placeholder="Search Contact"
                  className="Prompt"
                  value={props.term}
                  onChange={getSearchTerm}
              />
              <i className="fa fa-search"></i>
            </div>
          </div>

          {/* Add department filter buttons */}
          <div className="department-filters">
            <button onClick={() => filterByDepartment('BE.CSE')}>BE.CSE</button>
            <button onClick={() => filterByDepartment('BTECT')}>BTECT</button>
            <button onClick={() => filterByDepartment('')}>Clear Filter</button>
          </div>

          <div className="con-list">
            {renderContactList.length > 0 ? (
                renderContactList
            ) : (
                <div className="con-list-empty">No Contacts Found</div>
            )}
          </div>
        </div>
      </div>
  );
};

export default ContactList;
