import React from "react";
import { Link } from "react-router-dom";

class AddContact extends React.Component {
    state = {
        name: "",
        phoneNumber: "",
        email: "",
        password: "",
        photo: "",
        department: "",
        tableData: Array.from({ length: 8 }, () => Array(6).fill("")), // 8 rows, 6 columns
        errors: {
            name: "",
            phoneNumber: "",
            email: "",
            password: "",
            department: ""
        }
    };

    // Function to handle cell updates in the table
    handleTableChange = (rowIndex, colIndex, value) => {
        const newTableData = [...this.state.tableData];
        newTableData[rowIndex][colIndex] = value;
        this.setState({ tableData: newTableData });
    };

    validateInputs = () => {
        const { name, phoneNumber, email, password, department } = this.state;
        let errors = { name: "", phoneNumber: "", email: "", password: "", department: "" };
        let isValid = true;

        if (!/^[A-Za-z\s]+$/.test(name)) {
            errors.name = "Name should contain only alphabets and spaces";
            isValid = false;
        }

        if (!/^[0-9]{10}$/.test(phoneNumber)) {
            errors.phoneNumber = "Phone number must be exactly 10 digits";
            isValid = false;
        }

        if (!email.endsWith("@kongu.edu")) {
            errors.email = "Email should end with '@kongu.edu'";
            isValid = false;
        }

        if (!/(?=.*[0-9])(?=.*[!@#$%^&*])(?=.{6,})/.test(password)) {
            errors.password = "Password must contain at least one special character, one numeric, and be at least six characters long";
            isValid = false;
        }

        if (department === "") {
            errors.department = "Please select a department";
            isValid = false;
        }

        this.setState({ errors });
        return isValid;
    };

    add = (e) => {
        e.preventDefault();

        if (this.validateInputs()) {
            // Pass the new contact data including tableData to the parent handler
            this.props.addContactHandler(this.state);

            // Clear the form fields
            this.setState({
                name: "",
                phoneNumber: "",
                email: "",
                password: "",
                photo: "",
                department: "",
                tableData: Array.from({ length: 8 }, () => Array(6).fill("")),
                errors: { name: "", phoneNumber: "", email: "", password: "", department: "" }
            });
        }
    };

    render() {
        // Days and periods for the table
        const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
        const periods = Array.from({ length: 8 }, (_, i) => i + 1);
        const departments = ["BE.CSE", "BTech.IT", "BE.ECE", "BE.EEE"]; // Add more departments as needed

        return (
            <div className="container-contact2">
                <Link to="/">
                    <button className="btn3">{"<"}</button>
                </Link>
                <div className="wrap-contact2">
                    <form className="contact2-form" onSubmit={this.add}>
                        <span className="contact2-form-title">Add Contact</span>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Name"
                                type="text"
                                name="name"
                                value={this.state.name}
                                onChange={(e) => this.setState({ name: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="NAME"></span>
                            <span style={{ color: "red" }}>{this.state.errors.name}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Phone Number"
                                type="text"
                                name="phoneNumber"
                                value={this.state.phoneNumber}
                                onChange={(e) => this.setState({ phoneNumber: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="PHONE NUMBER"></span>
                            <span style={{ color: "red" }}>{this.state.errors.phoneNumber}</span>
                        </div>

                        <div className="wrap-input2">
                            <select
                                className="input2"
                                name="department"
                                value={this.state.department}
                                onChange={(e) => this.setState({ department: e.target.value })}
                            >
                                <option value="">Select Department</option>
                                {departments.map((dept, index) => (
                                    <option key={index} value={dept}>{dept}</option>
                                ))}
                            </select>
                            <span className="focus-input2" data-placeholder="DEPARTMENT"></span>
                            <span style={{ color: "red" }}>{this.state.errors.department}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Email"
                                type="text"
                                name="email"
                                value={this.state.email}
                                onChange={(e) => this.setState({ email: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="EMAIL"></span>
                            <span style={{ color: "red" }}>{this.state.errors.email}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Password"
                                type="password"
                                name="password"
                                value={this.state.password}
                                onChange={(e) => this.setState({ password: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="PASSWORD"></span>
                            <span style={{ color: "red" }}>{this.state.errors.password}</span>
                        </div>

                        <div className="wrap-input2">
                            <input
                                className="input2"
                                placeholder="Photo URL"
                                type="text"
                                name="photo"
                                value={this.state.photo}
                                onChange={(e) => this.setState({ photo: e.target.value })}
                            />
                            <span className="focus-input2" data-placeholder="PHOTO URL"></span>
                        </div>

                        {/* Editable 7x9 table */}
                        <table className="contact-table">
                            <thead>
                            <tr>
                                <th></th>
                                {daysOfWeek.map((day, index) => (
                                    <th key={index}>{day}</th>
                                ))}
                            </tr>
                            </thead>
                            <tbody>
                            {periods.map((period, rowIndex) => (
                                <tr key={rowIndex}>
                                    <td><strong>{period}</strong></td>
                                    {daysOfWeek.map((_, colIndex) => (
                                        <td key={colIndex}>
                                            <input
                                                type="text"
                                                value={this.state.tableData[rowIndex][colIndex] || ""}
                                                onChange={(e) =>
                                                    this.handleTableChange(rowIndex, colIndex, e.target.value)
                                                }
                                            />
                                        </td>
                                    ))}
                                </tr>
                            ))}
                            </tbody>
                        </table>

                        <button className="btn">Add</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default AddContact;
