import React, { useRef } from "react";
import { Link } from "react-router-dom";
import ContactCard from "./ContactCard";

const ContactList = (props) => {
    const inputE1 = useRef("");

    const deleteContactHandler = (id) => {
        props.getContactID(id);
    };

    const renderContactList = props.contacts.map((contact) => {
        return (
            <ContactCard
                contact={contact}
                clickHandler={deleteContactHandler}
                key={contact._id}
            />
        );
    });

    const getSearchTerm = () => {
        props.searchKeyword(inputE1.current.value);
    };

    const fillSearchWithDept = (dept) => {
        inputE1.current.value = dept;
        props.searchKeyword(dept);
    };

    return (
        <div>
            <div className="contact-list">
                <span className="contact2-form-title2">
                    Contact list
                    <Link to="/add" className="contact2-form-add">
                        <button className="btn2">Add contact</button>
                    </Link>
                </span>

                <div className="con-length">
                    Total Contacts: {props.contacts.length}
                </div>

                <div className="con-search">
                    <div className="con-search-input">
                        <input
                            ref={inputE1}
                            type="text"
                            placeholder="Search Contact"
                            className="Prompt"
                            value={props.term}
                            onChange={getSearchTerm}
                        />
                        <i className="fa fa-search"></i>
                    </div>
                    <div style={{
                        display: "flex",
                        justifyContent: "space-between", // Use "space-around" if needed
                        gap: "10px", // Space between buttons
                        width: "100%",
                        marginBottom: "10px"// Ensures buttons span across available space
                    }}>
                        <button
                            className="btn-fill-search btn2"
                            onClick={() => fillSearchWithDept("BE.CSE")}
                        >
                            BE.CSE
                        </button>

                        <button
                            className="btn-fill-search btn2"
                            onClick={() => fillSearchWithDept("BE.ECE")}
                        >
                            BE.ECE
                        </button>

                        <button
                            className="btn-fill-search btn2"
                            onClick={() => fillSearchWithDept("BTech.IT")}
                        >
                            BTech.IT
                        </button>
                        <button
                            className="btn-fill-search btn2"
                            onClick={() => fillSearchWithDept("")}
                        >
                            Clear filter
                        </button>
                    </div>
                </div>

                <div className="con-list">
                    {renderContactList.length > 0 ? (
                        renderContactList
                    ) : (
                        <div className="con-list-empty">No Contacts Found</div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ContactList;
