import React from "react";
import { Link } from "react-router-dom";

const ContactCard = (props) => {
    const { _id, name, phoneNumber, photo, department } = props.contact;
    const { userDetails } = props;

    // Determine if user has permission to edit/delete
    const hasPermission = userDetails.position === "HOD" && userDetails.department === department;

    const deleteContact = (id) => {
        fetch(`/api/contacts/${id}`, { method: 'DELETE' })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to delete contact');
                }
                return response.json();
            })
            .then(() => {
                props.clickHandler(id);
            })
            .catch((error) => {
                console.error('Error deleting contact:', error);
            });
    };

    return (
        <div className="item">
            <img
                className="image-avtar"
                src={photo || "https://www.gravatar.com/avatar/?d=mp"}
                alt="user"
            />
            <Link to={`/contact/${_id}`}>
                <div className="content">
                    <div className="item-name">{name}</div>
                    <div className="item-phone">{phoneNumber}</div>
                    <div className="item-department">{department}</div>
                </div>
            </Link>
            {hasPermission && (
                <>
                    <Link to={`/edit/${_id}`}>
                        <i className="edit-icon fa fa-edit" />
                    </Link>
                    <i
                        className="remove-icon fa fa-remove"
                        onClick={() => deleteContact(_id)}
                    />
                </>
            )}
        </div>
    );
};

export default ContactCard;
